import React, { useEffect, useRef } from "react";
import { useAppDispatch, useAppSelector } from "../../../../redux/hooks";
import { fetchProductsListAsync } from "./productSlice";
import List from '@mui/material/List';
import ListItem from '@mui/material/ListItem';
import ListItemText from '@mui/material/ListItemText';
import ListItemAvatar from '@mui/material/ListItemAvatar';
import Divider from '@mui/material/Divider';
import CircularProgress from '@mui/material/CircularProgress';

const ProductsList: React.FC<{ }> = () => {
  
  const dispatch = useAppDispatch();
  const ref = useRef<any>();

  async function getProductsList() {
    dispatch(fetchProductsListAsync());
  }

  const { products, loading } = useAppSelector(
    (state) => state.product
  );

  useEffect(() => {
    getProductsList()
  },[])

  function mouseEnter(direction : any){
    if(direction === "down"){
        ref.current.style.overflow = 'auto';
    }
  }

  function mouseLeave(){
    ref.current.style.overflow = 'hidden';
  }

  return (
    <div 
      ref={ref} 
      onMouseOver={() => mouseEnter('down')} 
      onMouseLeave={() => mouseLeave()}
      style={{
        position: 'relative',
        overflow: 'hidden',
        backgroundColor: 'white',
        width: '100%',
        maxWidth: 400,
        height: '40rem',
        maxHeight: '40rem',
        borderRadius: '.5rem'
      }}
    >
      <List
        sx={{
          width: '100%',
          maxWidth: 380
        }}
      >
      {
        loading ? (<div style={{
          height: '100%',
          alignItems: 'center',
          display: 'flex',
          justifyContent: 'center'
        }}>
        <CircularProgress />
      </div>) : products && products.length > 0 ? (
            <>
              {[...products].map((product: any, index: number) => (
                <>
                  <ListItem>
                    <ListItemAvatar>
                      <img
                          style={{
                            width: '40px',
                            height: '40px'
                          }}
                          src={product.image}
                          alt=""
                      />
                    </ListItemAvatar>
                    <ListItemText primary={product.title} secondary={product.description} />
                  </ListItem>
                  <Divider variant="inset" component="li" />
                </>
              ))}
            </>
          ) 
         : (<></>)
      }
    </List>
    </div>
  );
};

export default ProductsList;
