import React, { useEffect, useRef } from "react";
import { FixedSizeList, ListChildComponentProps } from 'react-window';
import { useAppDispatch, useAppSelector } from "../../../../redux/hooks";
import { fetchUsersListAsync } from "./userSlice";
import List from '@mui/material/List';
import ListItem  from '@mui/material/ListItem';
import ListItemButton  from '@mui/material/ListItemButton';
import ListItemText from '@mui/material/ListItemText';
import ListItemAvatar from '@mui/material/ListItemAvatar';
import Avatar from '@mui/material/Avatar';
import CircularProgress from '@mui/material/CircularProgress';
import ListSubheader from '@mui/material/ListSubheader';
import SearchBar from "../SearchBar";

const UsersList: React.FC<{ }> = () => {

  const dispatch = useAppDispatch();
  const ref = useRef<any>();

  async function getUsersList() {
    dispatch(fetchUsersListAsync());
  }

  const { users, loading } = useAppSelector(
    (state) => state.user
  );

  useEffect(() => {
    getUsersList()
  },[])

  function mouseEnter(direction : any){
    if(direction === "down"){
        ref.current.style.overflow = 'auto';
    }
  }

  function mouseLeave(){
    ref.current.style.overflow = 'hidden';
  }

  const handleToggle = (e: number) => () => {
    console.log("/////", e)
    // const currentIndex = checked.indexOf(value);
    // const newChecked = [...checked];

    // if (currentIndex === -1) {
    //   newChecked.push(value);
    // } else {
    //   newChecked.splice(currentIndex, 1);
    // }

    // setChecked(newChecked);
  };

  return (
    <div
      ref={ref} 
      onMouseOver={() => mouseEnter('down')} 
      onMouseLeave={() => mouseLeave()}
      style={{
        position: 'relative',
        overflow: 'hidden',
        backgroundColor: 'white',
        width: '100%',
        maxWidth: 380,
        height: '40rem',
        maxHeight: '40rem',
        borderRadius: '.5rem'
      }}
    >
      <List
        sx={{
          width: '100%',
          maxWidth: 350
        }}
        component="nav"
        aria-labelledby="nested-list-subheader"
        subheader={
          <ListSubheader component="div" id="nested-list-subheader">
            courses
          </ListSubheader>
        }
      >
        <SearchBar items={users} />
        {
          loading ? 
            (<div style={{
              height: '100%',
              alignItems: 'center',
              display: 'flex',
              justifyContent: 'center'
            }}>
            <CircularProgress />
          </div>) 
          : users && users.length > 0 ? (
              <>
                {[...users].map((user: any, index: number) => (
                  <ListItem >
                    <ListItemButton onClick={(e: any) => handleToggle(e)}>
                    <ListItemAvatar>
                      <Avatar>
                      </Avatar>
                    </ListItemAvatar>
                    <ListItemText primary={user.username} secondary={user.email} />
                  </ListItemButton >
                  </ListItem>
                  
                ))}
              </>
            ) 
          : (<></>)
        }
      </List>
    </div>
  );
};

export default UsersList;
