import React, { useState } from "react";
import Chip from '@mui/material/Chip';
import Stack from '@mui/material/Stack';
import { useAppDispatch, useAppSelector } from "../../../../redux/hooks";
import { removeChip } from "../../components/Chip/chipSlice";

const MyChip: React.FC<{ }> = () => {

  const dispatch = useAppDispatch();

  const { chips } = useAppSelector(
    (state) => state.chip
  );
  
  const handleDelete = (data: any, id: any) => {
    dispatch(removeChip({...data, id}))
  };

  return (
    <div style={{
      position: 'relative',
      overflow: 'hidden',
      backgroundColor: 'white',
      width: '100%',
      maxWidth: 350,
      height: '40rem',
      maxHeight: '40rem',
      borderRadius: '.5rem',
    }}>
      <div style={{width: '100%',
          maxWidth: 350}}>
        {
          chips.map((chip: any, index: number) => (
            <Chip 
              color="primary"
              variant="filled"
              size="medium"
              key={index} 
              label={chip.name} 
              onClick={() => handleDelete(chip, chip.id)}
              style={{margin: '5px'}}
            />
          ))
        }
      </div>
    </div>
  );
};

export default MyChip;
