import React, { useEffect } from "react";
import { useAppDispatch, useAppSelector } from "../../../../redux/hooks";
import { fetchProductsListAsync } from "./productSlice";
import List from '@mui/material/List';
import ListItem from '@mui/material/ListItem';
import ListItemText from '@mui/material/ListItemText';
import ListItemAvatar from '@mui/material/ListItemAvatar';
import Divider from '@mui/material/Divider';
import CircularProgress from '@mui/material/CircularProgress';

const ProductsList: React.FC<{ }> = () => {
  
  const dispatch = useAppDispatch();

  async function getProductsList() {
    dispatch(fetchProductsListAsync());
  }

  const { products, loading } = useAppSelector(
    (state) => state.product
  );

  useEffect(() => {
    getProductsList()
  },[])

  let scrollToken : any = null;

function mouseEnter(direction : any){
  scrollToken = setInterval(function(){
    if(direction === "down"){
      // Scroll down, e.g. by using scrollBy
    }else{
      // Scroll up, e.g. by using scrollBy
    }
  }, 3);
}

function mouseLeave(){
  clearInterval(scrollToken);
}

  return (
    <div onMouseOver={() => mouseEnter('down')} onMouseLeave={() => mouseLeave()}>
    <List 
      sx={{
        width: '100%',
        maxWidth: 400,
        bgcolor: 'background.paper',
        position: 'relative',
        overflow: 'auto',
        height: '40rem',
        maxHeight: '40rem',
        borderRadius: '.5rem'
      }}
    >
      {
        loading ? (<div style={{
          height: '100%',
          alignItems: 'center',
          display: 'flex',
          justifyContent: 'center'
        }}>
        <CircularProgress />
      </div>) : products && products.length > 0 ? (
            <>
              {[...products].map((product: any, index: number) => (
                <>
                  <ListItem>
                    <ListItemAvatar>
                      <img
                          style={{
                            width: '40px',
                            height: '40px'
                          }}
                          src={product.image}
                          alt=""
                      />
                    </ListItemAvatar>
                    <ListItemText primary={product.title} secondary={product.description} />
                  </ListItem>
                  <Divider variant="inset" component="li" />
                </>
              ))}
            </>
          ) 
         : (<></>)
      }
    </List>
    </div>
  );
};

export default ProductsList;
