import { IUserList } from "../../../../models/user.model";
import { createAsyncThunk, createSlice, PayloadAction } from "@reduxjs/toolkit";

export interface FirstListState {
  data: Array<IUserList>;
  loading: boolean;
  error: any;
}

const initialState: FirstListState = {
  loading: false,
  data: [],
  error: null,
};

export const fetchFirstListAsync = createAsyncThunk(
  "firstList/fetchFirstList",
  async (params: any) => {
    // const response: any = await monetizeServices.fetchApiProducts(params);
    // return response.data;
  }
);

export const firstListSlice = createSlice({
  name: "firstList",
  initialState,
  reducers: {},
  extraReducers: (builder) => {
    builder
      .addCase(fetchFirstListAsync.pending, (state) => {
        state.loading = true;
      })
      .addCase(fetchFirstListAsync.fulfilled, (state, action) => {
        state.loading = false;
        state.data = action.payload;
      })
      .addCase(fetchFirstListAsync.rejected, (state, action) => {
        state.loading = false;
        state.error = action.payload;
      });
  },
});

export const {} = firstListSlice.actions;

export default firstListSlice.reducer;
